css:
/*pc*/@media (min-width: 601px){
  .header__submenu {
    background: #efefef;
    border-top: 1px solid #ba5a6e;
    margin-top: 0;}
.header__search-input {
  border-radius: 20px;border: 1px solid #ddd;}
  .top-bar__page-links a {
    font-weight: 500; font-family: 'Nobile', sans-serif !important;
}
  .product__name{font-size: 26px;}
  .prices--big .prices__value {
    font-size: 30px;}
  h1 {
    font-size: 25px;font-family: "Raleway", sans-serif;color:#ba5a6e;
}
  h3 {font-size:20px;font-family: "Raleway", sans-serif;color:#ba5a6e;
  }

.header__logo {
    max-height: 90px;
    max-width: 300px;
}

}

  /*mobile*/@media (max-width: 600px) {
     .product__name{font-size: 21px;}.prices--big .prices__value {
    font-size: 25px;
  }
    h1 {
    font-size: 20px;font-family: "Raleway", sans-serif;
 color:#ba5a6e;
}
  h3 {font-size:18px;font-family: "Raleway", sans-serif;color:#ba5a6e;
}}
 /*responsivo*/
.info-bar{background: rgb(164,96,110);
background: -moz-linear-gradient(90deg, rgba(164,96,110,1) 0%, rgba(219,141,158,1) 35%, rgba(170,108,121,1) 100%);
background: -webkit-linear-gradient(90deg, rgba(164,96,110,1) 0%, rgba(219,141,158,1) 35%, rgba(170,108,121,1) 100%);
background: linear-gradient(90deg, rgba(164,96,110,1) 0%, rgba(219,141,158,1) 35%, rgba(170,108,121,1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#a4606e",endColorstr="#aa6c79",GradientType=1);}
.menu__item {align-items: center;border-bottom: var(--border-width) solid #ffffff;}
.products-carousel__title{font-family: "Raleway", sans-serif;
  color:#ba5a6e;}
.col-12.col-xl.order-lg-first p{ color:#fff;font-size:14px;font-family: 'Nobile', sans-serif !important;}
.footer__list-icon{color:#fff !important;}
.ul.footer__links{color:#fff!important;}
.footer__links a {color:#fff!important;}
.footer__links a:hover {color:#fff!important; text-decoration:none;}
.footer__contacts{color:#fff!important;}
.product__name{font-weight:400;font-family: 'Nobile', sans-serif !important;}
.col-12.col-sm-6.col-lg.col-xl-auto.px-xl-4{color:#f9e7e7 !important;}
.col-12.col-lg-auto.order-lg-last.pl-xl-4{color:#f9e7e7 !important;}
.product-card__offer-stamp {
    background-color: #ba5a6e;
  border-radius: 20px;}
.whatsapp-link {
    color: #5c5050!important;
}
.whatsapp-link a {color:#fff!important;}
.whatsapp-link a:hover {color:#fff!important;}
.product__discount {color: #ba5a6e;}
.share-network-whatsapp{display:none;}
.share-network-facebook{display:none;}
.share-network-twitter{display:none;}
.share-network-telegram{display:none;}
.share-network-pinterest{display:none;}
.share-network-email{display:none;}
.share-network-sms {display:none;}
#product-payment-header{display:none;}
.top-bar {background: #f7f7f7;width: 100%;}
.header {background: #ffffff;}
.body{font-family: 'Nobile', sans-serif;}
.header__toggler {color: #ffffff; font-size:0px;}
.footer {background:rgb(164,96,110);
background: -moz-linear-gradient(90deg, rgba(164,96,110,1) 0%, rgba(219,141,158,1) 35%, rgba(170,108,121,1) 100%);
background: -webkit-linear-gradient(90deg, rgba(164,96,110,1) 0%, rgba(219,141,158,1) 35%, rgba(170,108,121,1) 100%);
background: linear-gradient(90deg, rgba(164,96,110,1) 0%,
  rgba(219,141,158,1) 35%, rgba(170,108,121,1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient
  (startColorstr="#a4606e",endColorstr="#aa6c79",GradientType=1);
  border-top: var(--spacer-1) solid #transparent;
}
.footer__title { color: #ffffff;}
.footer__store {color: #fff;font-family: 'Nobile', sans-serif;
    font-weight: var(--font-headings);}
.footer__title[aria-expanded] {color:#fff;font-family: 'Nobile', sans-serif;}
.footer__links i {color: #dd99a7;}
.i-whatsapp{color: #fff;}
.i-facebook:before{color: #6c757d;}
.i-instagram:before{color: #6c757d;}
.i-youtube:before{color:#6c757d;}
.i-pinterest:before{color:#6c757d;}
.stamps img {max-width: 70%;}
.footer__credits {border-top: var(--border-width) solid #f7d9dd;}
.btn-primary {background-color: #ba5a6e;
    border-color: #ba5a6e;color: #fff;border-radius: 30px;}
.btn-success {background-color: #ba5a6e;
    border-color: #ba5a6e;color: #ffff;border-radius: 30px;}
.btn-success:hover  {background-color: #a70e3e;
    border-color: #a70e3e;color: #ffff;border-radius: 30px;}
 .btn-primary:hover {
    background-color: #bf2035;border-color: #bf2035;border-radius: 30px;
    color: #fff;}
.header__nav{font-size: 16px;font-weight: 300;text-transform: none;
  font-family: 'Nobile', sans-serif;margin-top: 10px;}
.header__nav a {background: transparent;font-size:16px;
  font-weight:300;font-family: 'Nobile', sans-serif;
 text-decoration: none;margin-bottom: 15px;color:#161616;}
  .header__nav a:hover {background: transparent;font-size: 16px;
    font-weight:400;font-family: 'Nobile', sans-serif;
 text-decoration: none;margin-bottom: 15px;color:#ba5a6e;}
.i-user{color: #6f6f6f;}
.i-search{color: #6f6f6f;}
.menu__footer a {color: #ba5a6e;text-decoration: none;}
.pb-lg-4{padding-bottom: 0rem;}
.btn-outline-secondary {border-color: #ba5a6e;
    color: #ba5a6e;border-radius: 30px;}
.btn-outline-secondary:hover{border-color: #a70e3e;
  background-color:#a70e3e;color: #fff;border-radius: 30px;}

.bt {background: #f1316e;
    padding: 10px 10px 10px 10px;
    margin: 20px 20px 20px 20px;
    border-radius: 30px;
    color: #fff;
    font-size: 18px;
    box-shadow: 1px 1px 14px #e0e0e0;}
.bt:hover {
    background: #a70e3e;
    padding: 10px 10px 10px 10px;
    margin: 20px 20px 20px 20px;
    border-radius: 30px;
    color: #fff;
    font-size: 18px;
    box-shadow: 1px 1px 14px #e0e0e0;}
.product__short-stock{display:none;}
.product-card__name{font-weight: 700; color:#000;}
.badge-warning {display:none;}

.product__unavailable{color: #ffffff;}
.prices__value {display: block;
    font-family: 'Nobile', sans-serif;font-size:16px;}
.product-card--inactive img {opacity: 100;}
.app__header {background: #fff;}
.i-tag{color:#ba5a6e;}

}
